    }

    const value = {
        products: state.productList,
        initialProductList,
    }
    return (
        <FilterContext.Provider value={value}>
            {children}
        </FilterContext.Provider>
    )
}

export const UseFilter = () => {
    const context = useContext(FilterContext)
    return context
}
